mapper.select:
select count(*) from tbl_activity a join tbl_user u on a.owner = u.id
a.name like '%' #{name} '%'
and u.name like '%' #{owner} '%'
and a.startDate > #{startDate}
and a.endDate < #{endDate}

select a.id, u.name as owner, a.name, a.startDate, a.endDate, a.cost, a.description, a.createTime, a.createBy, a.editTime, a.editBy
from tbl_activity a join tbl_user u on a.owner = u.id
where a.id = #{id}

select a.id, u.name as owner, a.name, a.startDate, a.endDate from tbl_activity a join tbl_user u on a.owner=u.id
where a.name like '%' #{name} '%' and a.id not in(
select activityId from tbl_clue_activity_relation where clueId=#{clueId}
)
